$(document).ready(function() { let e = new Date("4 May, 2021").getTime();
    setInterval(function() { let t = (new Date).getTime(),
            o = e - t,
            a = Math.floor(o / 864e5) + "d " + Math.floor(o % 864e5 / 36e5) + "h " + Math.floor(o % 36e5 / 6e4) + "m " + Math.floor(o % 6e4 / 1e3) + "s ";
        $("p#time").text(a) }, 500) });